	eq->mode = CH_LEFT;
	eq->low_freq = 400;
	eq->low_gain = EQ_GAIN_SD(st->parameter[9]);
	eq->high_freq = 4000;
	eq->high_gain = EQ_GAIN_SD(st->parameter[10]);
	info->panl = calc_pan_sd_left(st->parameter[11]);
	info->panr = calc_pan_sd_right(st->parameter[11]);
