	InfoEQ2 *eq = &info->eq;	
		
	// ring modulator
	rm->mode = CH_STEREO;
	rm->freq = (double)freq_table[clip_int(st->parameter[0], 0, 127)] * DIV_1000;
	//mod source st->parameter[1]
	//mod monitor st->parameter[2]
	//sens  st->parameter[3]
	//porality  st->parameter[4]
	rm->lfo_sw = 1; // lfo off
	//eq
