	pe->amp_sw = st->parameter[5] ? 1 : 0;
	amp->mode = CH_LEFT;
	amp->amp_sw = 1;
	amp->cab_sw = 1; // on
	switch(st->parameter[6]){
	default:
	case 0: // small
		amp->amp_type = 2;
		amp->cab_type = 1;
		break;
	case 1: // built in
		amp->amp_type = 4;
		amp->cab_type = 5;
		break;
	case 2: // 2 stack
		amp->amp_type = 8;
		amp->cab_type = 15;
		break;
	case 3: // 3 stack
		amp->amp_type = 9;
		amp->cab_type = 16;
		break;
	}
	amp->gain1 = 1.0;
	amp->gain2 = 1.0;
	amp->level = 1.0;
