	eq->width = (double)EQ_Q_SD(st->parameter[18]);
	// Enhancer
	pe->eh_sw = st->parameter[19] ? 1 : 0;
	eh->mode = CH_LEFT;	
	eh->hpf_cutoff = 3200;
	eh->sens = (double)clip_int(st->parameter[20], 0, 100) * DIV_100;	
